import "./App.css";
import { Howl, Howler } from "howler";
import { useEffect, useState } from "react";
import jsonp from "fetch-jsonp";
import OnAirInfo from "./OnAirInfo";
const sound = new Howl({
  src: ["http://icecast6.play.cz/casrock128.mp3"],
  html5: true,
});

function App() {
  const [radioImage, setRadioImage] = useState();
  const [radioTitle, setRadioTitle] = useState();
  const [radioPlay, setradioPlay] = useState(false);

  useEffect(() => {
    jsonp("http://api.play.cz/jsonp/getRadioInfo/casrock")
      .then((response) => response.json())
      .then((response) => {
        setRadioImage(response.data.basic.logo);
        setRadioTitle(response.data.basic.title);
      });
    Howler.volume(1);
  }, []);
  return (
    <div className='App'>
      <img className='App-radio-logo' src={radioImage} alt='logo' />
      <h1>{radioTitle}</h1>
      <div className='App-radio-list'></div>
      <div>
        <button className="App-play-button"
          onClick={() => {
            if (radioPlay === false){
              sound.play();
            }
            if (radioPlay === true) {
              sound.stop();
            }
          }}>
        </button>
        <button className="App-restart-button"
          onClick={() => {
            sound.stop();
            sound.play();
          }}>
        </button>
        <button className="App-stop-button"
          onClick={() => {
            sound.stop();
          }}>
          Stop
        </button>
      </div>

      <OnAirInfo />
    </div>
  );
}

export default App;
